parameters.jsonBody:
={
    "jsonrpc": "2.0",
    "method": "template.create",
    "params": {
      "host": "Example Template MySQL -{{ $('Exclude Special Characters').item.json.stdout }}",
      "name": "Example Template MySQL -{{ $('Exclude Special Characters').item.json.stdout }}",
      "description": "{{ $('Parent Workflow').item.json['Application Name'] }}",
      "groups": [
        { "groupid": "{{ $('Template Group ID').item.json.result[0].groupid }}" }
      ],
      "tags": [
	{ "tag": "NODATA", "value": "YES" },
        { "tag": "OBJECTCLASS", "value": "MYSQL" },
        { "tag": "OBJECTPARAMETER", "value": "DB Status" }
      ]
    },
    "id": 1
}

parameters.command:
=awk -F'[|]' '
BEGIN {
    print "<table border=\"1\">"
}
NR==1 {
    print "  <thead><tr>"
    for (i=1; i<=NF; i++) {
        if (i != 6 && i != 12 && i != 13 && i != 14) {
            printf "    <th>%s</th>\n", $i
        }
    }
    print "  </tr></thead>"
    print "  <tbody>"
    next
}
{
    print "  <tr>"
    for (i=1; i<=NF; i++) {
        if (i != 6 && i != 12 && i != 13 && i != 14) {
            printf "    <td>%s</td>\n", $i
        }
    }
    print "  </tr>"
}
END {
    print "  </tbody>"
    print "</table>"
}' {{ $('Parent Workflow').item.json.fileName }}.csv

